namespace BracketSpace\Notification\Signature\Dependencies\Symfony\Component\Console\CommandLoader;

use BracketSpace\Notification\Signature\Dependencies\Symfony\Component\Console\Command\Command;
use BracketSpace\Notification\Signature\Dependencies\Symfony\Component\Console\Exception\CommandNotFoundException;

/**
 * @author Robin Chalas <robin.chalas@gmail.com>
 */
interface CommandLoaderInterface
{
    /**
     * Loads a command.
     *
     * @return Command
     *
     * @throws CommandNotFoundException
     */
    public function get(string $name);

    /**
     * Checks if a command exists.
     *
     * @return bool
     */
    public function has(string $name);

    /**
     * @return string[]
     */
    public function getNames();
}
